import React from 'react';
// import logo from './logo.svg';
import Angle from './components/angle.component'
import './App.css';
import axios from 'axios';
// import {BrowserRouter as Router, Route} from 'react-router-dom;'
import "bootstrap/dist/css/bootstrap.min.css";

class App extends React.Component {
  state = {
    name: "circle",
    time: 0
  }

  componentDidMount(){
    axios.get("http://localhost:5000/teamMembers")
    .then((res) => {console.log(res.data)})
    .catch(err => {console.log("ERROR: " + err)});
  }

  startRotation = () => {
    const time = Math.floor(Math.random() * 10000) + 1;
    this.setState({
      time: time
    })
    console.log(time);
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },time);
  };

    render(){
      return (
        <div>
        <div className="arrow"></div>
        <ul className={this.state.name}>
          <Angle firstName="Jorie" lowerLimit="0" upperLimit="59" time={this.state.time}/>
          <li>
            <div className="text">Aaron</div>
          </li>
          <li>
            <div className="text">Lou</div>
          </li>
          <li>
            <div className="text">Steve</div>
          </li>
          <li>
            <div className="text">Erik</div>
          </li>
          <li>
            <div className="text">Day Drinking</div>
          </li>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}

export default App;
