import React from 'react';
// import logo from './logo.svg';
import './App.css';
// import {BrowserRouter as Router, Route} from 'react-router-dom;'
import "bootstrap/dist/css/bootstrap.min.css";
import axios from 'axios';



class App extends React.Component {
  state = {
    name: "circle",
    standups: null,
    lastTeamMemberId: null,
    lastStandup:null,
    otherTeamMembers: null,
    loading: true
  }

  startRotation = () => {
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },Math.floor(Math.random() * 10000) + 1);
  };

  async componentDidMount(){
    const url = "http://localhost:5000/dailyStandups";
    await axios.get(url).then((res) => {
      console.log(res.data);
    });
  }

    render(){
      return (
        <div>
        <div className="arrow"></div>
        <ul className={this.state.name}>
          <li>
            <div className="text" spellCheck="false">Jorie</div>
          </li>
          <li>
            <div className="text" spellCheck="false">Aaron</div>
          </li>
          <li>
            <div className="text" spellCheck="false">Lou</div>
          </li>
          <li>
            <div className="text" spellCheck="false">Steve</div>
          </li>
          <li>
            <div className="text" spellCheck="false">Erik</div>
          </li>
          <li>
            <div className="text" spellCheck="false">Day Drinking</div>
          </li>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}

export default App;
