import React from 'react';
// import logo from './logo.svg';
import './App.css';
// import {BrowserRouter as Router, Route} from 'react-router-dom;'
import "bootstrap/dist/css/bootstrap.min.css";

class App extends React.Component {
  state = {
    name: "circle"
  }

  startRotation = () => {
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },Math.floor(Math.random() * 10000) + 1);
  };

    render(){
      return (
        <div>
          <div>Who's On First?</div>
        <div className="arrow"></div>
        <ul className={this.state.name}>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Jorie</div>
          </li>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Aaron</div>
          </li>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Lou</div>
          </li>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Steve</div>
          </li>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Erik</div>
          </li>
          <li>
            <div className="text" contentEditable="true" spellCheck="false">Day Drinking</div>
          </li>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}

export default App;
