import {connect} from 'react-redux';
import * as Actions from '../actions';
import App from '../App';

const mapStateToProps = state => ({
    whosOnFirst: state.whosOnFirst,
    time : state.time,
    teamMembers: state.teamMembers
  });
  const mapDispatchToProps = dispatch => ({
    changeTMList: (value) => dispatch({type: Actions.changeTMList, value: value}),
    updateTimer: (value) => dispatch({type: Actions.changeTimeDelta, value: value}),
    updateTM: (value) => dispatch({type:Actions.changeTeamMember, value: value})

  });

  export default connect(
    mapStateToProps,
    mapDispatchToProps,
  )(App);
