import React from 'react';
// import logo from './logo.svg';
import Angle from './components/angle.component'
import './App.css';
import axios from 'axios';
// import {BrowserRouter as Router, Route} from 'react-router-dom;'
import "bootstrap/dist/css/bootstrap.min.css";

class App extends React.Component {
  state = {
    name: "circle",
    time: 0,
    testKey: '',
    teamMembers: null
  }

  componentDidMount(){
    axios.get("http://localhost:5000/teamMembers")
    .then((res) => {console.log(res.data);
    this.setState({
      teamMembers : res.data
    });
    })
    .catch(err => {console.log("ERROR: " + err)});
  }
CreateElements(){
    this.state.teamMembers.map((prop) => {
      return (
        <Angle firstName={prop.firstName}/>
      );
  });
  };

  startRotation = () => {
    const time = Math.floor(Math.random() * 10000) + 1;
    this.setState({
      time: this.state.time + time
    })
    console.log(time);
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },time);
  };

    render(){
      return (
        <div>
        <div className="arrow"></div>
        <ul className={this.state.name}>
          <CreateElements/>
          {/* <Angle firstName="Jorie" lowerLimit="0" upperLimit="59" time={this.state.time} data={this.state.testKey}/>
          <Angle firstName="Aaron" lowerLimit="60" upperLimit="119" time={this.state.time}/> */}
          <Angle firstName="Lou" lowerLimit="120" upperLimit="179" time={this.state.time}/>
          <Angle firstName="Steve" lowerLimit="180" upperLimit="239" time={this.state.time}/>
          <Angle firstName="Erik" lowerLimit="240" upperLimit="299" time={this.state.time}/>
          <Angle firstName="Day Drinking" lowerLimit="300" upperLimit="359" time={this.state.time}/>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}

export default App;
