import React from 'react';
// import logo from './logo.svg';
import Angle from './components/angle.component'
import './App.css';
import axios from 'axios';
// import {BrowserRouter as Router, Route} from 'react-router-dom;'
import "bootstrap/dist/css/bootstrap.min.css";
import { useSelector, useDispatch } from 'react-redux';
import {changeTimeDelta,changeTMList} from './actions';


class App extends React.Component {

  // state = {
  //   name: "circle",
  //   time: 0,
  //   testKey: '',
  //   teamMembers: []
  // }

  time = useSelector(state => state.time);
  componentDidMount(){

    axios.get("http://localhost:5000/teamMembers")
    .then((res) => {
      useDispatch(changeTMList(res.data));
    // this.setState({
    //   teamMembers : res.data
    // });
     console.log(this.state.teamMembers);
    })
    .catch(err => {console.log("ERROR: " + err)});
  }



  startRotation = () => {
    const time = Math.floor(Math.random() * 10000) + 1;
    // this.setState({
    //   time: this.state.time + time
    // })
    dispatch(changeTimeDelta(time));
    console.log(time);
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },time);
  };


    render(){

      const tms = this.state.teamMembers.map((props) => 
      <Angle key={props.firstName} firstName={props.firstName}/>
    );

      return (
        <div>
        <div className="arrow"></div>
        <ul className={this.state.name}>
          {tms}
          <Angle firstName="Day Drinking" lowerLimit="300" upperLimit="359" time={this.state.time}/>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}

export default App;
