    // });
    console.log("Response: " + res.data);
    this.props.changeTMList(res.data);
     console.log(this.state.teamMembers);
    })
    .catch(err => {console.log("ERROR: " + err)});
  }



  startRotation = () => {
    const time = Math.floor(Math.random() * 10000) + 1;
    // this.setState({
    //   time: this.state.time + time
    // })
    this.props.updateTimer(time);

    console.log(time);
    this.setState({
      name: "circle start-rotate"
    });
    setTimeout(() => {
      this.setState({
        name: "circle start-rotate stop-rotate"
      });

    },time);
  };


    render(){
      console.log(this.props);
    //   const tms = this.props.teamMembers.map((props) => 
    //   <Angle key={props.firstName} firstName={props.firstName}/>
    // );

      return (
        <div>
        <div className="arrow"></div>
        <ul className={this.props.name}>
          {/* {tms} */}
          <Angle firstName="Day Drinking" lowerLimit="300" upperLimit="359" time={this.props.time}/>
        </ul>
        <button className="spin-button" onClick={this.startRotation}>SPIN</button>
      </div>
      );

    }
}
export default App;
