import * as CalcFunctions from '../modules/CalculationManager';

const tmListReducer = (state = [], action) => {
    switch (action.type) {
      case "INITIALIZE":
        {
          const tmArray = Array.from(action.value.teamMembers);
           tmArray.forEach((aa) => {
            var i = tmArray.indexOf(aa);
            var n = i+1;
            var e = [];
            e[0] = aa;
            e[1] = (n-1)*60;
            e[2] = (n*60)-1;
            state[i] = e;
          });
          return state;       
        }

      case "UPDATE" : 
      {
        const tmArray = Array.from(action.value.teamMembers);
         tmArray.forEach((aa) => {
           aa[1] = CalcFunctions.CalculateAngleChange(action.value.time, aa[1]);
           aa[2] = CalcFunctions.CalculateAngleChange(action.value.time, aa[2]);
           action.value.teamMembers = tmArray;
      });
      return action.value;
    }
      default:
        return state;
    }
  };

  export default tmListReducer;
